import React, { Component } from 'react'
import './Form.css'

class Form extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);
    this.anotherHandleChange = this.anotherHandleChange.bind(this)

    this.state = {
      estadoFavorito: '',
      suaCidade:'',
    };

    this.state = {
      suaCidade:'',
    };
  }


  handleChange(event) {
    this.setState({
      estadoFavorito: event.target.value,
    });
  }

  anotherHandleChange(event) {
    this.setState({
    suaCidade: event.target.value,
    });
  }

  render() {
    return (
      <div>
        <h1>Estados e React - Tecnologia fant√°stica ou reagindo a regionalismos?</h1>
        <form className="form">
          <label>
            Diga qual o seu Estado favorito?
              <textarea name="estadoFavorito" value={this.state.estadoFavorito} onChange={this.handleChange} />
          </label>

          <label>
            Qual a sua cidade?
              <textarea name="suaCidade" value={this.state.suaCidade } onChange={this.anotherHandleChange} />
          </label>

          <input
            type="number"
            name="idade"
          />

          <input
            type="checkbox"
            name="vaiComparecer"
          />
        </form>
      </div>
    );
  }
}

export default Form;
